contracts/MultiAssetShieldedPool.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.24;

import {SpendVerifier} from "./verifiers/SpendVerifier.sol";
import {OutputVerifier} from "./verifiers/OutputVerifier.sol";
import {IMasp, Spend, Output, BridgeIn, BridgeOut} from "./interfaces/IMasp.sol";
import {MerkleTreeWithHistory} from "./MerkleTreeWithHistory.sol";

import "./EdOnBN254.sol";

contract MultiAssetShieldedPool is MerkleTreeWithHistory {
    using EdOnBN254 for *;

    SpendVerifier public spendVerifier;
    OutputVerifier public outputVerifier;

    mapping(uint256 => bool) public nullifierHashes;

    constructor(
        address _spendVerifier,
        address _outputVerifier,
        address _hasher
    ) MerkleTreeWithHistory(5, _hasher) {
        spendVerifier = SpendVerifier(_spendVerifier);
        outputVerifier = OutputVerifier(_outputVerifier);
        _initialize();
    }

    function parseProof(
        bytes memory data
    )
        internal
        pure
        returns (uint[2] memory a, uint[2][2] memory b, uint[2] memory c)
    {
        (a[0], a[1], b[0][0], b[0][1], b[1][0], b[1][1], c[0], c[1]) = abi
            .decode(data, (uint, uint, uint, uint, uint, uint, uint, uint));
    }

    function spendVerify(
        bytes memory _proof,
        uint[1] memory _pubSignals
    ) public view {
        (uint[2] memory a, uint[2][2] memory b, uint[2] memory c) = parseProof(
            _proof
        );
        require(
            spendVerifier.verifyProof(a, b, c, _pubSignals),
            "invalid proof"
        );
    }

    function outputVerify(
        bytes memory _proof,
        uint[1] memory _pubSignals
    ) public view {
        (uint[2] memory a, uint[2][2] memory b, uint[2] memory c) = parseProof(
            _proof
        );
        require(
            outputVerifier.verifyProof(a, b, c, _pubSignals),
            "invalid proof"
        );
    }

    struct RedDSASignature {
        bytes32 s;
        bytes32 R;
    }

    function _sigVerify(
        uint256 _s,
        uint256[2] memory _R,
        uint256[2] memory _A,
        bytes memory _message
    ) internal view {
        EdOnBN254.Affine memory _BASE = EdOnBN254.Affine(
            6822643173076850086669063981200675861034234425876310494228829770726075732893,
            9156654395656950371299901424185770236726741503478930161752204964343448620279
        );
        EdOnBN254.Affine memory _Rp = EdOnBN254.Affine(_R[0], _R[1]);
        EdOnBN254.Affine memory _Ap = EdOnBN254.Affine(_A[0], _A[1]);
        bytes memory data = abi.encode(_Rp.x, _Rp.y, _Ap.x, _Ap.y, _message);
        uint256 _c = uint256(keccak256(data)) % EdOnBN254.N;
        EdOnBN254.Affine memory _Z = _BASE.neg().mul(_s).add(_Rp).add(
            _Ap.mul(_c)
        );

        require(_Z.x == 0, "signature is not valid");
    }

    function _checkHash(
        Spend[] memory _spends,
        Output[] memory _outputs,
        bytes memory _hash
    ) internal pure {
        uint256[] memory nullifiers = new uint256[](_spends.length);
        uint256[] memory commitments = new uint256[](_outputs.length);
        uint256[] memory valueCommitments = new uint256[](
            _spends.length * 2 + _outputs.length * 2
        );

        uint256 vcIndex = 0;
        for (uint256 i = 0; i < _spends.length; i++) {
            nullifiers[i] = _spends[i].nullifier;
            valueCommitments[vcIndex++] = _spends[i].valueCommitment[0];
            valueCommitments[vcIndex++] = _spends[i].valueCommitment[1];
        }

        for (uint256 i = 0; i < _outputs.length; i++) {
            commitments[i] = _outputs[i].commitment;
            valueCommitments[vcIndex++] = _outputs[i].valueCommitment[0];
            valueCommitments[vcIndex++] = _outputs[i].valueCommitment[1];
        }

        require(
            keccak256(
                abi.encodePacked(nullifiers, commitments, valueCommitments)
            ) == bytes32(_hash),
            "Hashes must match"
        );
    }

    function toUint256ModN(
        int256 value,
        uint256 n
    ) internal pure returns (uint256) {
        if (value < 0) {
            require(uint256(-value) <= n, "Value must not be greater than n");
            return (n - uint256(-value)) % n;
        } else {
            require(uint256(value) <= n, "Value must not be greater than n");
            return uint256(value) % n;
        }
    }

    function _balanceCheck(
        Spend[] memory _ins,
        Output[] memory _outs,
        BridgeIn[] memory _bridgeIns,
        BridgeOut[] memory _bridgeOuts,
        EdOnBN254.Affine memory _extValueBase,
        EdOnBN254.Affine memory _bindingPubkey,
        int256 _extAmount
    ) internal view {
        // Sum up ins and outs

        EdOnBN254.Affine memory _insTotal = EdOnBN254.zero();
        EdOnBN254.Affine memory _outsTotal = EdOnBN254.zero();
        EdOnBN254.Affine memory _bridgeInsTotal = EdOnBN254.zero();
        EdOnBN254.Affine memory _bridgeOutsTotal = EdOnBN254.zero();

        for (uint i = 0; i < _ins.length; i++) {
            uint256[2] memory vc = _ins[i].valueCommitment;
            _insTotal = _insTotal.add(EdOnBN254.Affine(vc[0], vc[1]));
        }

        for (uint i = 0; i < _outs.length; i++) {
            uint256[2] memory vc = _outs[i].valueCommitment;
            _outsTotal = _outsTotal.add(EdOnBN254.Affine(vc[0], vc[1]));
        }

        for (uint i = 0; i < _bridgeIns.length; i++) {
            uint256[2] memory vc = _bridgeIns[i].valueCommitment;
            _bridgeInsTotal = _bridgeInsTotal.add(
                EdOnBN254.Affine(vc[0], vc[1])
            );
        }

        for (uint i = 0; i < _bridgeOuts.length; i++) {
            uint256[2] memory vc = _bridgeOuts[i].valueCommitment;
            _bridgeOutsTotal = _bridgeOutsTotal.add(
                EdOnBN254.Affine(vc[0], vc[1])
            );
        }

        EdOnBN254.Affine memory _ext = _extAmount == 0
            ? EdOnBN254.zero()
            : _extValueBase.mul(toUint256ModN(_extAmount, EdOnBN254.N));

        EdOnBN254.Affine memory total = _insTotal
            .add(_outsTotal.neg())
            .add(_bridgeOutsTotal)
            .add(_bridgeInsTotal.neg())
            .add(_ext);

        require(
            total.x == _bindingPubkey.x && total.y == _bindingPubkey.y,
            "Balance Check Failed"
        );
    }

    function _proofCheck(
        Spend[] memory _spends,
        Output[] memory _outputs,
        uint256 _root
    ) internal {
        require(isKnownRoot(bytes32(_root)), "Invalid merkle root");
        require(_outputs.length % 2 == 0, "outputs must be in multiples of 2");

        for (uint i = 0; i < _spends.length; i++) {
            require(!isSpent(_spends[i].nullifier), "Input is already spent");
        }

        for (uint i = 0; i < _spends.length; i++) {
            spendVerify(_spends[i].proof, [uint256(_spends[i].nullifier)]);
        }

        for (uint j = 0; j < _outputs.length; j++) {
            outputVerify(_outputs[j].proof, [uint256(_outputs[j].commitment)]);
        }

        for (uint i = 0; i < _spends.length; i++) {
            nullifierHashes[_spends[i].nullifier] = true;
        }

        for (uint i = 0; i < _outputs.length; i += 2) {
            _insert(
                bytes32(_outputs[i].commitment),
                bytes32(_outputs[i + 1].commitment)
            );
        }

        emit IMasp.NewCommitment(
            _outputs[0].commitment,
            nextIndex - 2,
            _outputs[0].encryptedOutput
        );

        emit IMasp.NewCommitment(
            _outputs[1].commitment,
            nextIndex - 1,
            _outputs[1].encryptedOutput
        );

        for (uint256 i = 0; i < _spends.length; i++) {
            emit IMasp.NewNullifier(_spends[i].nullifier);
        }
    }

    function _transact(
        Spend[] memory _spends,
        Output[] memory _outputs,
        BridgeIn[] memory _bridgeIns,
        BridgeOut[] memory _bridgeOuts,
        uint256 _extAssetHash,
        int256 _extAmount,
        uint256[2] memory _bpk,
        uint256 _root,
        uint256[2] memory _R,
        uint256 _s,
        bytes memory _hash
    ) internal {
        _checkHash(_spends, _outputs, _hash);

        EdOnBN254.Affine memory _bindingPubkey = EdOnBN254.Affine(
            _bpk[0],
            _bpk[1]
        );

        EdOnBN254.Affine memory _extValueBase = EdOnBN254
            .primeSubgroupGenerator()
            .mul(_extAssetHash);

        _balanceCheck(
            _spends,
            _outputs,
            _bridgeIns,
            _bridgeOuts,
            _extValueBase,
            _bindingPubkey,
            _extAmount
        );

        _sigVerify(_s, _R, _bpk, _hash);

        _proofCheck(_spends, _outputs, _root);
    }

    function isSpent(uint256 _nullifierHash) public view returns (bool) {
        return nullifierHashes[_nullifierHash];
    }
}

contracts/RK.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.24;

import "@chainlink/contracts-ccip/src/v0.8/ccip/interfaces/IRouterClient.sol";
import "@chainlink/contracts-ccip/src/v0.8/ccip/applications/CCIPReceiver.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./MultiAssetShieldedPool.sol";

contract RK is MultiAssetShieldedPool {
    address owner;

    constructor(
        address _spendVerifier,
        address _outputVerifier,
        address _merkleHasher
    ) MultiAssetShieldedPool(_spendVerifier, _outputVerifier, _merkleHasher) {
        owner = msg.sender;
    }

    function _enforceOwner() internal view {
        require(msg.sender == owner, "Only owner can call this function");
    }

    struct SupportedAsset {
        uint256 assetId;
        address assetAddress;
        uint8 decimals;
    }

    mapping(uint256 => SupportedAsset) public assetToAddress;

    function addSupportedAsset(
        uint256 _assetId,
        address _assetAddress,
        uint8 _decimals
    ) external {
        _enforceOwner();
        assetToAddress[_assetId] = SupportedAsset(
            _assetId,
            _assetAddress,
            _decimals
        );
    }

    function deposit(
        Spend[] calldata _spends,
        Output[] calldata _outputs,
        uint256[2] calldata _bpk,
        uint256 _assetId,
        int256 _depositAmount,
        uint256 _root,
        uint256[2] calldata _R,
        uint256 _s,
        bytes calldata _hash
    ) external {
        // transfer the users asset to this address
        SupportedAsset memory _asset = assetToAddress[_assetId];
        require(_asset.assetAddress != address(0), "Asset not supported");
        require(_depositAmount > 0);

        // transfer the asset to this contract
        IERC20(_asset.assetAddress).transferFrom(
            msg.sender,
            address(this),
            uint256(_depositAmount)
        );

               _transact(
            _spends,
            _outputs,
             new BridgeIn[](0),
            new BridgeOut[](0),
            _assetId,
            _depositAmount,
            _bpk,
            _root,
            _R,
            _s,
            _hash
        );
    }

    function withdraw(
        Spend[] calldata _spends,
        Output[] calldata _outputs,
        uint[2] calldata _bpk,
        uint256 _assetId,
        int256 _withdrawAmount,
        uint256 _root,
        uint256[2] calldata _R,
        uint256 _s,
        bytes calldata _hash
    ) external {
        SupportedAsset memory _asset = assetToAddress[_assetId];
        require(_asset.assetAddress != address(0), "Asset not supported");
        require(_withdrawAmount > 0);

        _transact(
            _spends,
            _outputs,
            new BridgeIn[](0),
            new BridgeOut[](0),
            _assetId,
            _withdrawAmount,
            _bpk,
            _root,
            _R,
            _s,
            _hash
        );

        // transfer the asset to this contract
        IERC20(_asset.assetAddress).transfer(
            msg.sender,
            uint256(_withdrawAmount)
        );
    }

    function transact(
        Spend[] calldata _spends,
        Output[] calldata _outputs,
        BridgeIn[] calldata _bridgeIns,
        BridgeOut[] calldata _bridgeOuts,
        uint256 _extAssetHash,
        int256 _extAmount,
        uint256[2] calldata _bpk,
        uint256 _root,
        uint256[2] calldata _R,
        uint256 _s,
        bytes calldata _hash
    ) external {
        _transact(
            _spends,
            _outputs,
            _bridgeIns,
            _bridgeOuts,
            _extAssetHash,
            _extAmount,
            _bpk,
            _root,
            _R,
            _s,
            _hash
        );
    }
}

